import axios from "axios";
import Constantes from "../../utils/Constantes";

const API_URL = Constantes.api_url;

// Primer peticion de ejemplo para traer usuarios

export async function getPosts() {
  return await axios({
    method: "GET",
    url: `${API_URL}/posts/posts/`,
  })
    .then((response) => {
        console.log("recibo datos");
        return response.data})
    .catch(function (error) {
        console.log('tus muertos')
      if (error.reponse) {
        return error.response;
      }
    });
    // return axios.get(`${API_URL}/usuario/usuario/`)
}

export async function getPostsByTema(idtema) {
  return await axios({
    method: "GET",
    url: `${API_URL}/posts/posts/${idtema}`,
  })
    .then((response) => {
        console.log("recibo datos");
        return response.data})
    .catch(function (error) {
        console.log('tus muertos')
      if (error.reponse) {
        return error.response;
      }
    });
    // return axios.get(`${API_URL}/usuario/usuario/`)
}


export async function getTemas() {
  return await axios({
    method: "GET",
    url: `${API_URL}/posts/temas/`,
  })
    .then((response) => {
        console.log("recibo datos");
        return response.data})
    .catch(function (error) {
        console.log('tus muertos')
      if (error.reponse) {
        return error.response;
      }
    });
    // return axios.get(`${API_URL}/usuario/usuario/`)
}
