import './scrollMenu.css'
import useDraggableScroll from 'use-draggable-scroll';
import { useEffect } from 'react';
import { useRef, useState } from 'react';
import { getTemas } from '../../services/PostsService';

const ScrollMenu = ({handleTemas}) =>{
    const ref = useRef(null);
    const { onMouseDown } = useDraggableScroll(ref,{direction: 'horizontal'});
    const [postsFiltrados,setPostsFiltrados] = useState([])
    const [temasTotales,setTemasTotales] = useState()
    // const [temaEsSeleccionado, setTemaEsSeleccionado] = useState(false)
    const [temaSeleccionado, setTemaSeleccionado] = useState('')
  
    
    //Traer la peticion de temas por peticion aqui que facilita los filtros

    const enviarTema = (e) => {
      handleTemas(e.target.innerHTML)
    }

    useEffect(() => {
      getTemas().then((res) => {
        setTemasTotales(res);
      });
  
    }, [])
    
    
    return (
      <div ref={ref} onMouseDown={onMouseDown} className="scroll-menu">
          {/* //Poner posible pipeline para nombres largos */}
        <div className="tema item1" onClick={enviarTema}>Pol√≠tica</div>
        <div className="tema item2" onClick={enviarTema}>Videojuegos</div>
        <div className="tema item3" onClick={enviarTema}>Cultura Internet</div>
        <div className="tema item4" onClick={enviarTema}>Tema 4</div>
        <div className="tema item5" onClick={enviarTema}>Tema 5</div>
        <div className="tema item6" onClick={enviarTema}>Tema 6</div>
        <div className="tema item7" onClick={enviarTema}>Tema 7</div>
        <div className="tema item8" onClick={enviarTema}>Tema 8</div>
        <div className="tema item9" onClick={enviarTema}>Tema 9</div>
        <div className="tema item10" onClick={enviarTema}>Tema 10</div>
      </div>
    );
}

export default ScrollMenu
