import "./home.css";
import Header from "../../components/molecules/Header/Header";
import Footer from "../../components/molecules/Footer/Footer";
import ScrollMenu from "../../components/molecules/ScrollMenu/ScrollMenu";
import Post from "../../components/molecules/Post/Post";
import { getUsuarios } from "../../components/services/UsersService";
import { getPosts, getPostsByTema, getTemas } from "../../components/services/PostsService";
import { useEffect, useState } from "react";

const Home = () => {
  const [usuarios, setUsuarios] = useState();
  const [posts, setPosts] = useState();
  const [postsFiltrados,setPostsFiltrados] = useState([])
  const [temasTotales,setTemasTotales] = useState()
  const [temaEsSeleccionado, setTemaEsSeleccionado] = useState(false)
  const [temaSeleccionado, setTemaSeleccionado] = useState('')
  
  const handleTemas = (e) => {
    setTemaSeleccionado(e)
    setTemaEsSeleccionado(true)
  }
 
  // const [token] = useLocalStorage('token','')
  useEffect(() => {
    getUsuarios().then((res) => {
      setUsuarios(res);
    });

    getTemas().then((res) => {
      setTemasTotales(res);
    });

    getPosts().then((res) => {
      setPosts(res);
    })

    if (temasTotales != null && temaSeleccionado != null) {
      temasTotales.forEach(tema => {
        if (tema.nombre == temaSeleccionado) {
          getPostsByTema(tema.id).then((res) => {
            setPostsFiltrados([])
            postsFiltrados.push(res);
          })
          console.log(postsFiltrados);
        }
        
      });
    }
  }, [temaEsSeleccionado,temaSeleccionado]);

  // console.log(usuarios);
  // console.log(posts);
  

  return (
    <>
      <div className="container-home d-flex flex-column ">
        <Header />

        <ScrollMenu 
          className="my-4" 
          handleTemas={handleTemas}
          />

        <main className="my-auto">
        {postsFiltrados != undefined ? 
          <>{console.log(postsFiltrados)}</>
          // postsFiltrados.map((post) => {
          //   <>
          //     <Post 
          //       key={post.id}
          //       className = "publicacion"
          //       postData={post}
          //     />
          //   </>
          // })
        :null}

        {/* {posts ? (
            posts.map((post) => (
            <>
              <Post 
                key={post.id}
                className = "publicacion"
                postData={post}
              />
            </>
            ))
        ):null} */}
        </main>
        <Footer />
      </div>
    </>
  );
};

export default Home;
