import './scrollMenu.css'
import useDraggableScroll from 'use-draggable-scroll';
import { useEffect } from 'react';
import { useRef } from 'react';

const ScrollMenu = ({handleTemas}) =>{
    const ref = useRef(null);
    const { onMouseDown } = useDraggableScroll(ref,{direction: 'horizontal'});
  
    
   
    useEffect(() => {
      handleTemas = (e) => { console.log(e.target.innerHTML)}
    }, [handleTemas])
    
    return (
      <div ref={ref} onMouseDown={onMouseDown} className="scroll-menu">
          {/* //Poner posible pipeline para nombres largos */}
        <div className="tema item1" onClick={handleTemas}>Pol√≠ica</div>
        <div className="tema item2" onClick={handleTemas}>Videojuegos</div>
        <div className="tema item3" onClick={handleTemas}>Cultura Internet</div>
        <div className="tema item4" onClick={handleTemas}>Tema 4</div>
        <div className="tema item5" onClick={handleTemas}>Tema 5</div>
        <div className="tema item6" onClick={handleTemas}>Tema 6</div>
        <div className="tema item7" onClick={handleTemas}>Tema 7</div>
        <div className="tema item8" onClick={handleTemas}>Tema 8</div>
        <div className="tema item9" onClick={handleTemas}>Tema 9</div>
        <div className="tema item10" onClick={handleTemas}>Tema 10</div>
      </div>
    );
}

export default ScrollMenu
